UseTemplate.CALLBACKCODE:
(M:Event) 'Lock' scmp 0 == if{
  1 (>O:_Limit_Needs_Recalculations)
  1 (>O:_Interacting)
  quit
}
(M:Event) 'UnLock' scmp 0 == if{
  0 (>O:_Gate_Up)
  0 (>O:_Gate_Down)
  0 (>O:_Interacting)
  quit
}
(M:Event) 'LeftSingle' 'RightSingle' (M:InputType) 1 == ? scmp 0 == if{
  (B:ENGINE_Throttle_Feathering, Bool) ! (B:ENGINE_Throttle, percent) #ILDE_ANIM_POS# - abs 20 < and if{ (>B:ENGINE_Throttle_High_Idle) quit }
  (B:ENGINE_Throttle_Feathering, Bool) (B:ENGINE_Throttle, percent) 100 - abs 20 < and if{ (>B:ENGINE_Throttle_Idle) quit }
}
(M:Event) 'LeftDrag' scmp 0 == if{
  (O:_ResetLimitsTime) (E:SIMULATION TIME, second) < if{
    (* Reset limits *)
    0 (>O:_Gate_Up)
    0 (>O:_Gate_Down)
    (M:InputType) 1 == if{ 1 (>O:_Limit_Needs_Recalculations) }
  }
  (E:SIMULATION TIME, second) 0.1 + (>O:_ResetLimitsTime)
  (B:ENGINE_Throttle_Feathering, Bool) if{
    (M:InputType) 1 == if{
      -0.5 0.5 (M:Y) rng ! if{
        (M:Y) 0.5 > if{
          (O:_Gate_Up) ! if{ 1 (>B:ENGINE_Throttle_Inc) 1 (>O:_Gate_Up) }
        } els{
          (O:_Gate_Down) ! if{ 1 (>B:ENGINE_Throttle_Dec) 1 (>O:_Gate_Down) }
        }
      }
    } els{
      0.15 0.35 (M:DragPercent) rng if{
        (>B:ENGINE_Throttle_Low_Idle)
      } els{
        (M:DragPercent) 0.25 > if{
          (>B:ENGINE_Throttle_High_Idle)
        } els{
          (>B:ENGINE_Throttle_Cutoff)
        }
      }
    }
  } els{
    (O:_Limit_Needs_Recalculations) if{
      (B:ENGINE_Throttle, percent) #ILDE_ANIM_POS# - abs 5 < ! if{
        0 (>O:_Limit_Needs_Recalculations)
        (B:ENGINE_Throttle, percent) #ILDE_ANIM_POS# > if{
          #UPPER_LIMIT# (>O:_Upper_Limit)
          0 (>O:_Lower_Limit)
        } els{
          0 (>O:_Upper_Limit)
          #LOWER_LIMIT# (>O:_Lower_Limit)
        }
      } els{
        #UPPER_LIMIT# (>O:_Upper_Limit)
        #LOWER_LIMIT# (>O:_Lower_Limit)
      }
    }
    0 16384 #LOWER_LIMIT# - (M:DragPercent) #ADJUST_RAW_DRAG_VALUE# * #LOWER_LIMIT# + #UPPER_LIMIT# / (O:_Upper_Limit) min (O:_Lower_Limit) max (>B:ENGINE_Throttle_Set)
  }
}

UseInputEvent.SET_STATE_EXTERNAL:
(O:XMLVAR_ThrottleIsDisconnected) if{
  (O:FUEL_Manual_Override_Position) @ManualOverrideTreshold <= if{
    0 (>O:XMLVAR_ThrottleIsDisconnected)
    (B:ENGINE_Throttle_Feathering, Bool) if{
      @SetToFeatheredSim
      (B:ENGINE_Throttle, percent) 25 > if{ @OpenFuelValve }
      15 35 (B:ENGINE_Throttle, percent) rng if{
        @SetToConditionPosIdleLO_Code
      } els{
        (B:ENGINE_Throttle, percent) 25 > if{
          @SetToConditionPosIdleHI_Code
        } els{
          @SetToConditionPosCutoff_Code
        }
      }
    } els{
      @SetToUnFeatheredSim
      (B:ENGINE_Throttle, percent over 100) 1 (A:THROTTLE LOWER LIMIT, percent over 100) - * (A:THROTTLE LOWER LIMIT, percent over 100) + 16384 * (>K:THROTTLE1_SET)
    }
  } els{
    @SetToUnFeatheredSim
    (O:FUEL_Manual_Override_Position) @ManualOverrideTreshold - 100 @ManualOverrideTreshold - / 100 * @ConvertTo16k (>K:THROTTLE1_SET)
  }
} els{
  (O:FUEL_Manual_Override_Position) @ManualOverrideTreshold > if{
    1 (>O:XMLVAR_ThrottleIsDisconnected)
  }
}

